listitem.programlisting:
// Задача с ограничением 10 секунд и с текущим окном в качестве родителя
final BackgroundTask<Integer, Void> progressIndicator = new BackgroundTask<Integer, Void>(10, this) {
    @Override
    public Void run(TaskLifeCycle<T> taskLifeCycle) {
        // 1 2 3 4 5 :-)
        for (int i = 0; i < 5; i++) {
            Long res;
            try {
                TimeUnit.SECONDS.sleep(1);
            } catch (InterruptedException ignored) {
                return null;
            }
            // Оглашаем прогресс
            taskLifeCycle.publish(i);
        }
        return null;
    }

    @Override
    public void canceled() {
        // отменено
    }

    @Override
    public void done(Void result) {
        // завершено
    }

    @Override
    public void progress(List<Integer> changes) {
        // индикация прогресса
    }

    @Override
    public Map<String, Object> getParams() {
        // передаём параметры
        return Collections.emptyMap();
    }
};

// Получение управляющего объекта и запуск
BackgroundTaskHandler taskHandler = backgroundWorker.handle(progressIndicator);
taskHandler.execute();
